DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250811142717_AddUsersAndRoomOwner') THEN
    ALTER TABLE "Messages" ADD CONSTRAINT "FK_Messages_Rooms_RoomId1" FOREIGN KEY ("RoomId1") REFERENCES "Rooms" ("Id");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250811142717_AddUsersAndRoomOwner') THEN
    ALTER TABLE "Rooms" ADD CONSTRAINT "FK_Rooms_Users_OwnerId" FOREIGN KEY ("OwnerId") REFERENCES "Users" ("Id") ON DELETE SET NULL;
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20250811142717_AddUsersAndRoomOwner') THEN
